import { useState } from "react";

export default function App(){

  const [items,setItems] = useState([])

  function handleAddItems(item){
    setItems(items => [...items,item])
  }

  function handleDeleteItems(id){
    setItems(items=>items.filter(item => item.id !== id))
  }

  function handleToggleItem(id){
    setItems(items=>items.map(item => item.id === id ? { ...item ,packed : !item.packed} : item ))
  }

  return <div className="app">
  <Logo />
  <Form onAddItems={handleAddItems}/>
  <PackageList  items={items} OnDeleteItems={handleDeleteItems}/>
  <Stats />
  </div>
}

function Logo(){
  return <h1>üèùÔ∏è Far Away üß≥</h1>
}
function Form({onAddItems}){

  const [description,setDescription] = useState("")
  const [quantity,setQuantity] = useState(1)



  function handleSubmit(e){
    e.preventDefault();

    if(!description) return
    const newItem = {description,quantity,id:Date.now(),packed:false }
    console.log(newItem)

    setDescription("")
    setQuantity(1)
    onAddItems(newItem)
  }

  return (<form className="add-form" onSubmit={handleSubmit}>
    <h3>What do you need for your üòç trip?</h3>
    <select  value={quantity} onChange={(e)=> 
      setQuantity(Number(e.target.value))}>
      {Array.from({length:20},(_,i) => i+1).map(num => <option value={num} key={num}>{num}</option>)}
    </select>
    <input type="text" placeholder="Item..." value={description} onChange={(e)=> 
      setDescription(e.target.value)}></input>
    <button>Add</button>
  </form>)
}
function PackageList({items,OnDeleteItems}){
  return <div className="list">
    <ul>{items.map(item => <Item  OnDeleteItems={OnDeleteItems}  item={item} key = {item.id}/>)}</ul>
  </div>
}

function Item({item,OnDeleteItems}){
  return <li>
    <input type="checkbox" value={item.packed} onChange={()=>{}} />
    <span style={item.packed ? {textDecoration:"line-through" } : {}} >{item.quantity} {item.description}</span>
    <button onClick={()=>OnDeleteItems(item.id)}>‚ùå</button>
  </li>
}

function Stats(){
  return <footer className="stats">
    <em>üíºYou have X items on your list, and you already packed X (X%)</em>
  </footer>
}
