import { useState } from "react";
import { Logo } from "./Logo";
import { Form } from "./Form";

export default function App(){

  const [items,setItems] = useState([])

  function handleClearList(){
    const confirm = window.confirm('Are you sure you want to clear all items?')
    if (confirm )setItems([])
  }

  function handleAddItems(item){
    setItems(items => [...items,item])
  }

  function handleDeleteItems(id){
    setItems(items=>items.filter(item => item.id !== id))
  }

  function handleToggleItem(id){
    setItems(items=>items.map(item => item.id === id ? { ...item ,packed : !item.packed} : item ))
  }

  return <div className="app">
  <Logo />
  <Form onAddItems={handleAddItems}/>
  <PackageList  items={items} OnDeleteItems={handleDeleteItems} onToggleItems={handleToggleItem} onClearList={handleClearList} />
  <Stats items = {items} />
  </div>
}

function PackageList({items,OnDeleteItems,onToggleItems,onClearList}){
  const [sortBy,setSortBy] = useState("description")
  let sortedItems;
  if(sortBy === "input") sortedItems = items
  if (sortBy === "description") sortedItems = items.slice().sort((a, b) => a.description.localeCompare(b.description));
if (sortBy === "packed") sortedItems = items.slice().sort((a, b) => Number(a.packed) - Number(b.packed));

  return <div className="list">
    <ul>{sortedItems.map(item => <Item  OnDeleteItems={OnDeleteItems} onToggleItems={onToggleItems} item={item} key = {item.id}/>)}</ul>
    <div className="actions">
      <select value={sortBy} onChange={e=>setSortBy(e.target.value)}>
        <option value="input">Sort by input order</option>
        <option value="description">Sort by description</option>
        <option value="packed">Sort by packed status</option>
      </select>
      <button onClick={onClearList}>Clear list</button>
    </div>
  </div>
}

function Item({item,OnDeleteItems,onToggleItems}){
  return <li>
    <input type="checkbox" value={item.packed} onChange={()=>{onToggleItems(item.id)}} />
    <span style={item.packed ? {textDecoration:"line-through" } : {}} >{item.quantity} {item.description}</span>
    <button onClick={()=>OnDeleteItems(item.id)}>❌</button>
  </li>
}

function Stats({items}){

const numItems = items.length;
const numPacked = items.filter(item=>item.packed).length;
const percentage = Math.round((numPacked / numItems) *100);
// const percentage = ((numPacked / numItems) *100).toFixed(2);

  if(!items.length)
    return <footer className="stats">
    <em>
    Start adding some items to yout list 🚀
    </em>
  </footer>


  return <footer className="stats">
    <em>
    {percentage === 100 ? 'You got everything! Ready to go ✈️' :
   ` 💼 You have ${numItems} items on your list, and you already packed  ${numPacked} (${percentage}%)`}
    </em>
  </footer>
}
